import logo from './logo.svg';
import './App.css';
import './login.css'
import Login from './Login'
import { BrowserRouter, Routes, Route} from 'react-router-dom';
import Home from './Home';
import { useEffect, useState } from 'react';

function App() {
  const [check, setCheck] = useState(localStorage.getItem('logindash'));

  useEffect(() => {
    setCheck(localStorage.getItem('logindash'));
  }, []); // Empty dependency array to run only once on component mount

  return (
    <div className="App">
      <BrowserRouter>

        <Routes>
          <Route path="/" element={check ? <Home/> : <Login/>} />
          <Route path='/home' element = {check ? <Home/> : <Login  />}/>
        </Routes>

      </BrowserRouter>

    </div>
  );
}

export default App;
