import './App.css';
import './login.css';
import React, { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Login from './Login';
import Home from './Home';

function App() {
  const [check, setCheck] = useState(JSON.parse(localStorage.getItem('logindash')));

  useEffect(() => {
    setCheck(JSON.parse(localStorage.getItem('logindash')));
    console.log(check);
  }, [check]);

  return (
    <div className="App">
      <BrowserRouter>
        <Routes>
          <Route path="/" element={check ? <Home /> : <Login />} />
          <Route path='/home' element={check ? <Home /> : <Login />} />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
